import React from 'react';
import styled from 'styled-components';
import posed, { PoseGroup } from 'react-pose'

const menuFinderContainerPose = {
  raised: {
    y: '-50vh',
  },
  lowered: {
    y: '0vh',
  }
}

const MenuFinderContainer = styled(posed.div(menuFinderContainerPose))`
  position: relative;
`;

const menuFinderListPose = {
  enter: {
    opacity: 1,
    delayChildren: 200,
    staggerChildren: 50,
    delay: 300
  },
  exit: {
    opacity: 0,
  }
}

const CitySelector = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const MenuFinderList = styled(posed.div(menuFinderListPose))`
  opacity: 0;
`;

const menuFinderListItemPose = {
  enter: {
    y: ({ i }) => i * 0,
    opacity: 1,
    delay: ({ i }) => i * 900
  },
  exit: {
    y: ({ i }) => (i + 1) * 80,
    opacity: 0,
  },
}

const MenuFinderListItem = styled(posed.div(menuFinderListItemPose))`
  padding: 16px;
  background: #fff;
  margin-bottom: 16px;
`

const MenuFinderMenu = styled.div`

`;

const MenuFinder = ({ city, restaurants, handleRestaurantSelect, menu }) => (
  <MenuFinderContainer pose={city ? 'raised' : 'lowered'}>
    <CitySelector>
      Find restaurant menus in
    </CitySelector>
  </MenuFinderContainer>
);

export default MenuFinder;
