import React from 'react';
import styled from 'styled-components';
import posed, { PoseGroup } from 'react-pose'

const MenuFinderContainer = styled.div`

`;

const MenuFinderList = styled.div`

`;

const MenuFinderListItem = styled.div`
  width: 100%;
  padding: 16px;
  box-sizing: border-box;
  background: #fff;
  border-radius: 8px;
`;

const MenuFinderMenu = styled.div`

`;

const MenuFinder = ({ restaurants, handleRestaurantSelect, menu }) => (
  <MenuFinderContainer>
    <PoseGroup>
      {!menu
        ?
        <MenuFinderList key="1">
          {restaurants.map((restaurant) =>
              <MenuFinderListItem key="username">
                {restaurant.name}
              </MenuFinderListItem>
          )}
        </MenuFinderList>
        :
        <MenuFinderMenu key="2">
          Menu
        </MenuFinderMenu>
      }

    </PoseGroup>
  </MenuFinderContainer>
);

export default MenuFinder;
