import React from 'react';
import styled from 'styled-components';
import posed, { PoseGroup } from 'react-pose'
import Select from 'react-select';

const MenuFinderContainer = styled.div`
  position: relative;
`;

const menuFinderListPose = {
  enter: {
    opacity: 1,
    delayChildren: 200,
    staggerChildren: 50,
    delay: 300
  },
  exit: {
    opacity: 0,
  }
}

const citySelectorPose = {
  collapsed: {
    height: 'calc(100vh - 421px)',
  },
  expanded: {
    height: '100vh',
  }
}

const CitySelector = styled(posed.div(citySelectorPose))`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-flow: column nowrap;
  align-items: center;
  justify-content: center;

  & .dimension-div {
    display: inline-flex;
    flex-flow: column nowrap;
    align-items: center;
    justify-content: center;
  }
`;

const HeroText = styled.h1`
  font-size: 3rem;
  font-weight: 700;
  color: #fff;
  margin: 0;
  margin-bottom: 16px;
`;

const MenuPicker = styled.section`
  width: 100%;
  height: 100%;
  background: #fff;
  min-height: 449px;
  padding: 16px;
  box-sizing: border-box;
  display: flex;
  flex-flow: column nowrap;
  align-items: center;
  justify-content: flex-start;
`;

const MenuList = styled.div`
  display: inline-flex;
  flex-flow: column nowrap;
`;

const MenuListItem = styled.article`
  width: 100%;
  max-width: 420px;
  padding: 16px;
  box-sizing: border-box;
  border: 1px solid #fff;
  border-radius: 8px;
  background: #fff;
  display: flex;
  flex-flow: column nowrap;
  cursor: pointer;
  transition: padding-left 200ms cubic-bezier(0.0, 0, 0.2, 1) 0ms,border-color 200ms cubic-bezier(0.0, 0, 0.2, 1) 0ms,border-width 200ms cubic-bezier(0.0, 0, 0.2, 1) 0ms;
  margin-bottom: 16px;

  &:hover {
    border-color: #000;
  }
`;

const MenuListItemTitle = styled.h3`
  font-size: 1.5rem;
  font-weight: 700;
  color: rgba(0,0,0,1);
  margin: 0;
`;

const MenuListItemSubtitle = styled.span`
  font-size: 1rem;
  font-weight: 500;
  color: rgba(0,0,0,.64);
`;

const selectStyles = {
  control: (provided) => ({
    ...provided,
    borderRadius: 8,
    borderColor: '#fff',
    fontSize: '2.25rem',
    height: 56,
    width: 240,
    fontWeight: 700,
    color: '#000',
  })
}

function MenuFinder({ offsetHeight, cities, city, handleCitySelect, restaurants, handleRestaurantSelect, menu }) {
  let containerInput = React.createRef();
  console.log(containerInput);
  return (
  <MenuFinderContainer>
    <CitySelector pose={city ? 'collapsed' : 'expanded'} offsetHeight={offsetHeight}>
      <div className="dimension-div" ref={containerInput}>
        <HeroText onClick={() => console.log(containerInput.current.offsetHeight)}>
        Menus for restaurants in
        </HeroText>
        <Select
          value={cities.find((option) => option.value == city)}
          options={cities}
          styles={selectStyles}
          isClearable
          onChange={(option) => handleCitySelect(option, containerInput.current.offsetHeight)}
        />
      </div>
    </CitySelector>
    <MenuPicker>
      <MenuList>
        {restaurants.map((restaurant) =>
          <MenuListItem onClick={() => handleRestaurantSelect(restaurant.username)}>
            <MenuListItemTitle>
              {restaurant.name}
            </MenuListItemTitle>
            <MenuListItemSubtitle>
              Southern · $$ - $$$ · 1669 Barrington St.
            </MenuListItemSubtitle>
          </MenuListItem>
        )}
      </MenuList>
    </MenuPicker>
  </MenuFinderContainer>
)};

export default MenuFinder;
