import React from 'react';
import styled from 'styled-components';
import posed, { PoseGroup } from 'react-pose'
import Select from 'react-select';

const menuFinderContainerPose = {
  raised: {
    y: '-50vh',
  },
  lowered: {
    y: '0vh',
  }
}

const MenuFinderContainer = styled(posed.div(menuFinderContainerPose))`
  position: relative;
`;

const menuFinderListPose = {
  enter: {
    opacity: 1,
    delayChildren: 200,
    staggerChildren: 50,
    delay: 300
  },
  exit: {
    opacity: 0,
  }
}

const CitySelector = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-flow: column nowrap;
  align-items: center;
  justify-content: center;
`;

const HeroText = styled.h1`
  font-size: 3rem;
  font-weight: 700;
  color: #fff;
  margin: 0;
  margin-bottom: 16px;
`;

const MenuPicker = styled.div`
  width: 100%;
  height: 100%;
  background: #fff;
  min-height: 50vh;
`;

const selectStyles = {
  control: (provided) => ({
    ...provided,
    borderRadius: 8,
    borderColor: '#fff',
    fontSize: '2.25rem',
    height: 56,
    width: 240,
    fontWeight: 700,
    color: '#000',
  })
}

const MenuFinder = ({ cities, city, handleCitySelect, restaurants, handleRestaurantSelect, menu }) => (
  <MenuFinderContainer pose={city ? 'raised' : 'lowered'}>
    <CitySelector>
      <HeroText>
      Menus for restaurants in
      </HeroText>
      <Select
        value={city}
        options={cities}
        styles={selectStyles}
        onChange={(option) => handleCitySelect(option)}
      />
    </CitySelector>
    <MenuPicker>
      Test
    </MenuPicker>
  </MenuFinderContainer>
);

export default MenuFinder;
