import React from 'react';
import styled from 'styled-components';
import posed, { PoseGroup } from 'react-pose'

const MenuFinderContainer = styled.div`

`;

const menuFinderListPose = {
  visible: {
    opacity: 1,
    delayChildren: 200,
    staggerChildren: 50,
  },
  hidden: {
    opacity: 0
  }
}

const MenuFinderList = styled(posed.div(menuFinderListPose))`

`;

const menuFinderListItemPose = {
  visible: {
    y: 0,
    opacity: 1,
  },
  hidden: {
    y: -24,
    opacity: 0,
  }
}

const MenuFinderListItem = styled(posed.div(menuFinderListItemPose))`
  width: 100%;
  padding: 16px;
  box-sizing: border-box;
  background: #fff;
  border-radius: 8px;
`;

const MenuFinderMenu = styled.div`

`;

const MenuFinder = ({ restaurants, handleRestaurantSelect, menu }) => (
  <MenuFinderContainer>
    <PoseGroup>
      {!menu
        ?
        <MenuFinderList key="1" pose="visible">
          {restaurants.map((restaurant) =>
              <MenuFinderListItem key="username">
                {restaurant.name}
              </MenuFinderListItem>
          )}
        </MenuFinderList>
        :
        <MenuFinderMenu key="2">
          Menu
        </MenuFinderMenu>
      }

    </PoseGroup>
  </MenuFinderContainer>
);

export default MenuFinder;
