import React, { Component } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import posed from 'react-pose'

import MenuFinder from '../components/MenuFinder'

const cities = [
  {
    label: 'Halifax',
    value: 'Halifax'
  }
]

const GlobalStyle = createGlobalStyle`
  body {
    margin: 0;
    padding: 0;
    font-family: 'Source Sans Pro', sans-serif;
  }
`

const IndexWrapper = styled.div`
  width: 100%;
  height: 100vh;
  max-height: 100vh;
  margin: 0;
  padding: 0;
  background-image: url(https://images.unsplash.com/photo-1491739481003-2991327b66e2?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1654&q=80);
  background-size: cover;
  background-position: center;
  box-sizing: border-box;
  display: flex;
  flex-flow: column nowrap;
  position: absolute;
  top: 0;
  right: 0;
  bottom: 0;
  left: 0;
  overflow: hidden;
`;

const Nav = styled.div`
  width: 100%;
  max-width: 100vw;
  min-width: 100vw;
  height: 96px;
  padding: 0 32px;
  box-sizing: border-box;
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-between;
  align-items: center;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
`;

const NavButton = styled.button`
  border: 0;
  border-radius: 8px;
  appearance: none;
  background: #fff;
  font-size: 1rem;
  font-weight: 700;
  color: #000;
  padding: 12px 16px;
`;

const heroPose = {
  lowered: {
    y: ({ height }) => (`calc(50vh - 72px - ${height / 2}px) `)
  },
  raised: {
    y: '0vh'
  }
}

const Hero = styled(posed.div(heroPose))`
  display: flex;
  flex-flow: column nowrap;
  align-items: center;
`;

const HeroText = styled.h1`
  font-size: 3rem;
  font-weight: 700;
  color: #fff;
  margin: 0;
  margin-bottom: 16px;
`;

const ContentArea = styled.div`
  width: 100%;
  padding: 16px;
  box-sizing: border-box;
  display: flex;
  flex: 1;
`;

const customSelectStyles = {
  control: (provided) => ({
    ...provided,
    borderRadius: 8,
    borderColor: '#fff',
    fontSize: '2.25rem',
    height: 56,
    width: 240,
    fontWeight: 700,
    color: '#000',
  })
}

class Index extends Component {
  constructor(props) {
    super(props);
    this.state = {
      menu: "",
      restaurants: [
        {
          name: 'Julep Kitchen + Bar',
          username: 'julepkitchenbar'
        },
        {
          name: 'XOH',
          username: 'xoh'
        },
      ],
      raised: false,
      city: ''
    }
  }

  componentDidMount() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(displayLocationInfo);
    }

    function displayLocationInfo(position) {
      const lng = position.coords.longitude;
      const lat = position.coords.latitude;

      console.log(`longitude: ${ lng } | latitude: ${ lat }`);
    }
  }

  handleGeolocation(position) {
    console.log('Geolocation');
    console.log(position);
  }

  handleCitySelect(value, offsetHeight) {
    console.log(value);
    if (value) {
      this.setState({
        city: value.value,
        offsetHeight,
      })
    } else {
      this.setState({
        city: ""
      })
    }
  }

  render() {
    return (
      <IndexWrapper>
        <GlobalStyle />
        <Nav>
          <span style={{ color: '#fff'}}>
            our <br/>menu
          </span>
          <NavButton>
            For restaurants
          </NavButton>
        </Nav>
        <MenuFinder
          cities={cities}
          city={this.state.city}
          handleCitySelect={(option) => this.handleCitySelect(option)}
          restaurants={this.state.restaurants}
          handleRestaurantSelect={(restaurant) => this.setState({ restaurant })}
          offsetHeight={this.state.offsetHeight} />
      </IndexWrapper>
    );
  }

}

export default Index;
