import React from 'react';
import styled from 'styled-components';
import posed, { PoseGroup } from 'react-pose'

const MenuFinderContainer = styled.div`

`;

const menuFinderListPose = {
  enter: {
    opacity: 1,
    delayChildren: 200,
    staggerChildren: 50,
    delay: 300
  },
  exit: {
    opacity: 0,
  }
}

const MenuFinderList = styled(posed.div(menuFinderListPose))`
  opacity: 0;
`;

const menuFinderListItemPose = {
  enter: {
    y: 0,
    opacity: 1,
    delay: ({ i }) => i * 500
  },
  exit: {
    y: -360,
    opacity: 0,
  },
}

const MenuFinderListItem = posed.div(menuFinderListItemPose)

const MenuFinderMenu = styled.div`

`;

const MenuFinder = ({ restaurants, handleRestaurantSelect, menu }) => (
  <MenuFinderContainer>
    <PoseGroup animateOnMount>
      {restaurants.map((restaurant, i) =>
        <MenuFinderListItem key="username" i={i}>
          {restaurant.name}
        </MenuFinderListItem>
      )}
    </PoseGroup>
  </MenuFinderContainer>
);

export default MenuFinder;
