import React from 'react';
import styled from 'styled-components';
import posed, { PoseGroup } from 'react-pose'

const menuFinderContainerPose = {
  raised: {
    y: '-50vh',
  },
  lowered: {
    y: '0vh',
  }
}

const MenuFinderContainer = styled(posed.div(menuFinderContainerPose))`
  position: relative;
`;

const menuFinderListPose = {
  enter: {
    opacity: 1,
    delayChildren: 200,
    staggerChildren: 50,
    delay: 300
  },
  exit: {
    opacity: 0,
  }
}

const CitySelector = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const MenuPicker = styled.div`
  width: 100%;
  height: 100%;
  background: #fff;
  min-height: 50vh;
`;

const MenuFinder = ({ city, restaurants, handleRestaurantSelect, menu }) => (
  <MenuFinderContainer pose={city ? 'raised' : 'lowered'}>
    <CitySelector>
      Find restaurant menus in
    </CitySelector>
    <MenuPicker>
      Test
    </MenuPicker>
  </MenuFinderContainer>
);

export default MenuFinder;
