import React from 'react';
import styled from 'styled-components';
import posed, { PoseGroup } from 'react-pose'
import Select from 'react-select';

const MenuFinderContainer = styled.div`
  position: relative;
`;

const menuFinderListPose = {
  enter: {
    opacity: 1,
    delayChildren: 200,
    staggerChildren: 50,
    delay: 300
  },
  exit: {
    opacity: 0,
  }
}

const citySelectorPose = {
  collapsed: {
    height: 'calc(100vh - 421px)',
  },
  expanded: {
    height: '100vh',
  }
}

const CitySelector = styled(posed.div(citySelectorPose))`
  width: 100%;
  height: 100vh;
  display: flex;
  flex-flow: column nowrap;
  align-items: center;
  justify-content: center;
`;

const HeroText = styled.h1`
  font-size: 3rem;
  font-weight: 700;
  color: #fff;
  margin: 0;
  margin-bottom: 16px;
`;

const MenuPicker = styled.section`
  width: 100%;
  height: 100%;
  background: #fff;
  min-height: 449px;
  padding: 16px;
  box-sizing: border-box;
`;

const MenuList = styled.div`
  display: flex;
  flex-flow: column nowrap;
`;

const MenuListItem = styled.article`
  width: 100%;
  max-width: 420px;
  padding: 16px;
  box-sizing: border-box;
  border: 1px solid #fff;
  border-radius: 8px;
  background: #fff;
  display: flex;
  flex-flow: column nowrap;
`;

const MenuListItemTitle = styled.h3`

`;

const selectStyles = {
  control: (provided) => ({
    ...provided,
    borderRadius: 8,
    borderColor: '#fff',
    fontSize: '2.25rem',
    height: 56,
    width: 240,
    fontWeight: 700,
    color: '#000',
  })
}

const MenuFinder = ({ cities, city, handleCitySelect, restaurants, handleRestaurantSelect, menu }) => (
  <MenuFinderContainer>
    <CitySelector pose={city ? 'collapsed' : 'expanded'}>
      <HeroText>
      Menus for restaurants in
      </HeroText>
      <Select
        value={cities.find((option) => option.value == city)}
        options={cities}
        styles={selectStyles}
        isClearable
        onChange={(option) => handleCitySelect(option)}
      />
    </CitySelector>
    <MenuPicker>
      {restaurants.map((restaurant) =>
        <MenuListItem>
          {restaurant.name}
        </MenuListItem>
      )}
    </MenuPicker>
  </MenuFinderContainer>
);

export default MenuFinder;
