import React from 'react';
import styled from 'styled-components';
import posed, { PoseGroup } from 'react-pose'

const MenuFinderContainer = styled.div`

`;

const menuFinderListPose = {
  enter: {
    opacity: 1,
    delayChildren: 200,
    staggerChildren: 50,
    delay: 300
  },
  exit: {
    opacity: 0,
  }
}

const MenuFinderList = styled(posed.div(menuFinderListPose))`
  opacity: 0;
`;

const menuFinderListItemPose = {
  enter: {
    y: ({ i }) => (i + 1) * 80 + 'px',
    opacity: 1,
    delay: ({ i }) => i * 900
  },
  exit: {
    y: ({ i }) => `calc((${i} + 1) * -100)%`,
    opacity: 0,
  },
}

const MenuFinderListItem = styled(posed.div(menuFinderListItemPose))`
  padding: 16px;
  background: #fff;
  margin-bottom: 16px;
`

const MenuFinderMenu = styled.div`

`;

const MenuFinder = ({ restaurants, handleRestaurantSelect, menu }) => (
  <MenuFinderContainer>
    <PoseGroup animateOnMount>
      {restaurants.map((restaurant, i) =>
        <MenuFinderListItem key={restaurant.username} i={i}>
          {restaurant.name}
        </MenuFinderListItem>
      )}
    </PoseGroup>
  </MenuFinderContainer>
);

export default MenuFinder;
