import React, { Component } from 'react';
import styled, { createGlobalStyle } from 'styled-components';
import posed from 'react-pose'
import Select from 'react-select';

const GlobalStyle = createGlobalStyle`
  body {
    margin: 0;
    padding: 0;
    font-family: 'Montserrat', sans-serif;
  }
`

const IndexWrapper = styled.div`
  width: 100%;
  height: 100vh;
  max-height: 100vh;
  margin: 0;
  padding: 0;
  background-image: url(https://images.unsplash.com/photo-1555813456-94a3dd418cd3?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjF9&auto=format&fit=crop&w=1252&q=80);
  background-size: cover;
  background-position: center;
  box-sizing: border-box;
  display: flex;
  flex-flow: column nowrap;
`;

const Nav = styled.div`
  width: 100%;
  max-width: 100vw;
  min-width: 100vw;
  height: 72px;
  padding: 16px;
  box-sizing: border-box;
  display: flex;
  flex-flow: row nowrap;
  justify-content: space-between;
`;

const NavButton = styled.button`
  border: 0;
  border-radius: 8px;
  appearance: none;
  background: #fff;
  font-size: 1rem;
  font-weight: 600;
  color: #000;
  padding: 8px 16px;
`;

const heroPose = {
  lowered: {
    y: ({ height }) => (`calc(50vh - 72px - ${height}px) `)
  },
  raised: {
    y: '0vh'
  }
}

const Hero = styled(posed.div(heroPose))`
  display: flex;
  flex-flow: column nowrap;
  align-items: center;
`;

const HeroText = styled.h1`
  font-size: 2.5rem;
  font-weight: 600;
  color: #fff;
  margin: 0;
`;

const ContentArea = styled.div`
  width: 100%;
  padding: 16px;
  box-sizing: border-box;
  display: flex;
  flex: 1;
`;



class Index extends Component {
  constructor(props) {
    super(props);
    this.state = {
      restaurant: {},
      restaurants: [],
      raised: false,
      heroHeight: 49
    }
    this.heroRef = React.createRef();
  }

  componentDidMount() {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(displayLocationInfo);
    }

    function displayLocationInfo(position) {
      const lng = position.coords.longitude;
      const lat = position.coords.latitude;

      console.log(`longitude: ${ lng } | latitude: ${ lat }`);
    }
    console.log(this.heroRef.current.offsetHeight);
    this.setState({
      heroHeight: this.heroRef.current.offsetHeight
    })
  }

  handleGeolocation(position) {
    console.log('Geolocation');
    console.log(position);
  }

  render() {
    return (
      <IndexWrapper>
        <GlobalStyle />
        <Nav>
          <span>
            Our menu
          </span>
          <NavButton>
            For restaurants
          </NavButton>
        </Nav>
        <Hero pose={this.state.raised ? 'raised' : 'lowered'} ref={this.heroRef} height={this.state.heroHeight}>
          <HeroText>Find restaurant menus in</HeroText>
        </Hero>
          {this.state.raised &&
            <ContentArea>
              Test
            </ContentArea>
          }
      </IndexWrapper>
    );
  }

}

export default Index;
